            writer.WriteNumber("id", cardLedgerTransactionLogEntry.Id);

            if (cardLedgerTransactionLogEntry.ApplicationIdOption.IsSet)
                writer.WriteNumber("applicationId", cardLedgerTransactionLogEntry.ApplicationIdOption.Value.Value);

            if (cardLedgerTransactionLogEntry.SessionIdOption.IsSet)
                writer.WriteNumber("sessionId", cardLedgerTransactionLogEntry.SessionIdOption.Value.Value);

            if (cardLedgerTransactionLogEntry.CustomerSessionIdOption.IsSet)
                writer.WriteString("customerSessionId", cardLedgerTransactionLogEntry.CustomerSessionId);
        }
    }
}
