                JsonSerializer.Serialize(writer, messageLogEntry.Request, jsonSerializerOptions);
            }
            if (messageLogEntry.ResponseOption.IsSet)
            {
                writer.WritePropertyName("response");
                JsonSerializer.Serialize(writer, messageLogEntry.Response, jsonSerializerOptions);
            }
            if (messageLogEntry.UrlOption.IsSet)
                writer.WriteString("url", messageLogEntry.Url);

            if (messageLogEntry.ApplicationIdOption.IsSet)
                writer.WriteNumber("applicationId", messageLogEntry.ApplicationIdOption.Value.Value);

            if (messageLogEntry.LoyaltyProgramIdOption.IsSet)
                writer.WriteNumber("loyaltyProgramId", messageLogEntry.LoyaltyProgramIdOption.Value.Value);

            if (messageLogEntry.CampaignIdOption.IsSet)
                writer.WriteNumber("campaignId", messageLogEntry.CampaignIdOption.Value.Value);
        }
    }
}
