            if (account.ApplicationLimitOption.IsSet)
                writer.WriteNumber("applicationLimit", account.ApplicationLimitOption.Value.Value);

            if (account.UserLimitOption.IsSet)
                writer.WriteNumber("userLimit", account.UserLimitOption.Value.Value);

            if (account.CampaignLimitOption.IsSet)
                writer.WriteNumber("campaignLimit", account.CampaignLimitOption.Value.Value);

            if (account.ApiLimitOption.IsSet)
                writer.WriteNumber("apiLimit", account.ApiLimitOption.Value.Value);

            if (account.AttributesOption.IsSet)
            {
                writer.WritePropertyName("attributes");
                JsonSerializer.Serialize(writer, account.Attributes, jsonSerializerOptions);
            }
        }
    }
}
