            if (integrationEventV3Request.PreviousEventIDOption.IsSet)
                writer.WriteString("previousEventID", integrationEventV3Request.PreviousEventID);

            if (integrationEventV3Request.LoyaltyCardsOption.IsSet)
            {
                writer.WritePropertyName("loyaltyCards");
                JsonSerializer.Serialize(writer, integrationEventV3Request.LoyaltyCards, jsonSerializerOptions);
            }
            if (integrationEventV3Request.ResponseContentOption.IsSet)
            {
                writer.WritePropertyName("responseContent");
                JsonSerializer.Serialize(writer, integrationEventV3Request.ResponseContent, jsonSerializerOptions);
            }
        }
    }
}
